import{_ as a,j as e,k as r,S as t}from"./chunks/framework.2ff57350.js";const o=JSON.parse('{"title":"币傲交易所","description":"","frontmatter":{},"headers":[],"relativePath":"log/币傲交易所.md","filePath":"log/币傲交易所.md"}'),l={name:"log/币傲交易所.md"},h=[t('<h1 id="币傲交易所" tabindex="-1">币傲交易所 <a class="header-anchor" href="#币傲交易所" aria-label="Permalink to &quot;币傲交易所&quot;">​</a></h1><h2 id="背景" tabindex="-1">背景 <a class="header-anchor" href="#背景" aria-label="Permalink to &quot;背景&quot;">​</a></h2><p>​ 虚拟货币最初的设计初衷是为了成为一种去中心化的数字货币，旨在解决传统货币不可逆、高手续费、可伪造和资金跨境转移等问题。然而随着时间的推移，虚拟货币的价值成为了交易和炒作的工具，一些投机者和投资者开始将其作为理财、投机甚至是赌博的手段。这是因为虚拟货币本身没有内在价值支撑，而价格完全由市场供求关系决定，容易受到市场情绪、投机炒作等因素影响，导致价格波动巨大，非常容易被操纵，从而成了某些人的炒作目标。</p><p>​ 相比之下，真正有价值的是虚拟货币背后的区块链技术。区块链技术是一种去中心化的、不可篡改的数据记录技术，可以应用到金融、物流、医疗等领域，让数据更加安全、公正、透明和高效。然而，由于区块链技术资瓷的是分布式系统，以及因为应用范围及技术限制等问题，区块链到目前为止只进入了有限的应用场景。</p><h2 id="架构" tabindex="-1">架构 <a class="header-anchor" href="#架构" aria-label="Permalink to &quot;架构&quot;">​</a></h2><p><img src="https://c18e-1257416358.cos.accelerate.myqcloud.com/%E5%B8%81%E5%82%B2%E4%BA%A4%E6%98%93%E6%89%80%E6%9E%B6%E6%9E%84.png" alt="币傲交易所架构"></p><h2 id="看板" tabindex="-1">看板 <a class="header-anchor" href="#看板" aria-label="Permalink to &quot;看板&quot;">​</a></h2><p>​</p><h2 id="组织过程资产" tabindex="-1">组织过程资产 <a class="header-anchor" href="#组织过程资产" aria-label="Permalink to &quot;组织过程资产&quot;">​</a></h2><ol><li><a href="https://docs.qq.com/sheet/DQVZzSnNXSXVwclJu?tab=BB08J2" target="_blank" rel="noreferrer">bug列表</a></li></ol>',10)];const i=a(l,[["render",function(a,t,o,l,i,n){return e(),r("div",null,h)}]]);export{o as __pageData,i as default};
