import{_ as s,o as a,c as n,a as l}from"./app.a5d386c5.js";const e=JSON.parse('{"title":"Ubantu 服务器常见问题","description":"","frontmatter":{},"headers":[{"level":3,"title":"Ubuntu简介","slug":"ubuntu简介","link":"#ubuntu简介","children":[]},{"level":2,"title":"Q:如何快速登录服务器？","slug":"q-如何快速登录服务器","link":"#q-如何快速登录服务器","children":[]},{"level":2,"title":"Q:如何安装MYSQL并远程连接NAVCAT?","slug":"q-如何安装mysql并远程连接navcat","link":"#q-如何安装mysql并远程连接navcat","children":[]},{"level":2,"title":"Q:如何安装python包管理工具，并使用虚拟目录管理环境？","slug":"q-如何安装python包管理工具-并使用虚拟目录管理环境","link":"#q-如何安装python包管理工具-并使用虚拟目录管理环境","children":[]},{"level":2,"title":"Q:服务器免密登录github配置","slug":"q-服务器免密登录github配置","link":"#q-服务器免密登录github配置","children":[]},{"level":2,"title":"Q：如何解决ubuntu环境下uwsgi启动报错问题？","slug":"q-如何解决ubuntu环境下uwsgi启动报错问题","link":"#q-如何解决ubuntu环境下uwsgi启动报错问题","children":[]},{"level":2,"title":"Q:Ubantu 如何日志文件常用操作有哪些？","slug":"q-ubantu-如何日志文件常用操作有哪些","link":"#q-ubantu-如何日志文件常用操作有哪些","children":[]},{"level":2,"title":"Q: 如何申请免费证书","slug":"q-如何申请免费证书","link":"#q-如何申请免费证书","children":[]},{"level":2,"title":"Q: SSH一直超时 nginx 网址一直超时","slug":"q-ssh一直超时-nginx-网址一直超时","link":"#q-ssh一直超时-nginx-网址一直超时","children":[]}],"relativePath":"Q&A/Ubuntu服务器常见问题.md"}'),p={name:"Q&A/Ubuntu服务器常见问题.md"},o=[l('<h1 id="ubantu-服务器常见问题" tabindex="-1">Ubantu 服务器常见问题 <a class="header-anchor" href="#ubantu-服务器常见问题" aria-hidden="true">#</a></h1><h3 id="ubuntu简介" tabindex="-1">Ubuntu简介 <a class="header-anchor" href="#ubuntu简介" aria-hidden="true">#</a></h3><p>Ubuntu近年来最热的操作系统。基于非常正统的Debian发行版，使用和Debian一样便捷的软件包管理。这个管理发行机制被认为是Linux/Unix下设计 最为完善的。它的apt-get和apitute命令， 不仅可以和yum一样，非常迅速的安装软件，还会自动设置用户和用户组的以及其他相关的配置信息，即使不是IT专业人士，对者手册或者Google, 也能像模像样的建起一个基本的服务器环境。这点体贴对于缺少IT专业支持的小公司而言，还是非常有帮助的。和Cent OS主要依靠社区提供支持不同，Ubuntu的背后是Canoical，这个公司本身也没什么，但它的老板是Mark Shuttleworth，偶像级的创业典范。他的数字认证公司曾经一度占据全球50％的份额，后来在1999年以5.75亿美元卖给了 VeriSign。当时他才刚从大学毕业3年，只有24岁！！</p><p>​ 他还是全球第二个自费的太空游客，关于他的详细事迹，请看这里。简单的说，这个老板是年轻，懂技术，有理想有追求又不缺钱的人，你愿意用他公司的产品吗？</p><h2 id="q-如何快速登录服务器" tabindex="-1">Q:如何快速登录服务器？ <a class="header-anchor" href="#q-如何快速登录服务器" aria-hidden="true">#</a></h2><ul><li><h4 id="默认端口登录" tabindex="-1">默认端口登录 <a class="header-anchor" href="#默认端口登录" aria-hidden="true">#</a></h4><p><code>ssh USER@SERVER_IP</code>（默认22端口）</p></li><li><h4 id="指定端口登录" tabindex="-1">指定端口登录 <a class="header-anchor" href="#指定端口登录" aria-hidden="true">#</a></h4><p><code>ssh -p PORT USER@SERVER_IP</code>（指定端口）</p></li><li><h4 id="免密登录服务" tabindex="-1">免密登录服务 <a class="header-anchor" href="#免密登录服务" aria-hidden="true">#</a></h4><ol><li><h5 id="创建密钥文件并将公钥传输给服务器" tabindex="-1">创建密钥文件并将公钥传输给服务器 <a class="header-anchor" href="#创建密钥文件并将公钥传输给服务器" aria-hidden="true">#</a></h5><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki material-theme-palenight" tabindex="0"><code><span class="line"><span style="color:#A6ACCD;">cd ~/.ssh/ &amp;&amp; ssh-keygen -t rsa -f KEY_NAME;</span></span>\n<span class="line"><span style="color:#A6ACCD;">//公钥传输给服务器 </span></span>\n<span class="line"><span style="color:#A6ACCD;">scp KEY_NAME.pub USER@SERVER_IP:.;</span></span>\n<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div></li><li><h5 id="进入服务器-公钥加入authorized-keys" tabindex="-1">进入服务器 公钥加入authorized_keys <a class="header-anchor" href="#进入服务器-公钥加入authorized-keys" aria-hidden="true">#</a></h5><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki material-theme-palenight" tabindex="0"><code><span class="line"><span style="color:#A6ACCD;">ssh USER@SERVER_IP ;</span></span>\n<span class="line"><span style="color:#A6ACCD;">cat ~/KEY_NAME.pub &gt;&gt; ~/.ssh/authorized_keys;</span></span>\n<span class="line"><span style="color:#A6ACCD;">重启sshd</span></span>\n<span class="line"><span style="color:#A6ACCD;">systemctl restart sshd;</span></span>\n<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div></li><li><h5 id="本地添加免密登录并验证" tabindex="-1">本地添加免密登录并验证 <a class="header-anchor" href="#本地添加免密登录并验证" aria-hidden="true">#</a></h5><div class="language-shell"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki material-theme-palenight" tabindex="0"><code><span class="line"><span style="color:#FFCB6B;">在~/.ssh/config</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">文件添加以下内容</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#FFCB6B;">Host</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">SERVER_NAME</span></span>\n<span class="line"><span style="color:#A6ACCD;">   </span><span style="color:#FFCB6B;">HostName</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">SERVER_IP</span><span style="color:#A6ACCD;">           </span><span style="color:#C3E88D;">//服务器ip</span></span>\n<span class="line"><span style="color:#A6ACCD;">   </span><span style="color:#FFCB6B;">port</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">SERVER_PORT</span><span style="color:#A6ACCD;">             </span><span style="color:#C3E88D;">//默认的端口</span><span style="color:#A6ACCD;"> </span><span style="color:#F78C6C;">22</span></span>\n<span class="line"><span style="color:#A6ACCD;">   </span><span style="color:#FFCB6B;">User</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">USER</span><span style="color:#A6ACCD;">                    </span><span style="color:#C3E88D;">//用户名</span></span>\n<span class="line"><span style="color:#A6ACCD;">   </span><span style="color:#FFCB6B;">IdentityFile</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">~/.ssh/KEY_NAME</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">//私钥文件</span></span>\n<span class="line"><span style="color:#A6ACCD;">   </span></span>\n<span class="line"></span></code></pre></div></li><li><h5 id="使用ssh-server-name-进入服务区验证是否成功" tabindex="-1">使用<code>ssh SERVER_NAME </code> 进入服务区验证是否成功 <a class="header-anchor" href="#使用ssh-server-name-进入服务区验证是否成功" aria-hidden="true">#</a></h5></li></ol></li></ul><h2 id="q-如何安装mysql并远程连接navcat" tabindex="-1">Q:如何安装MYSQL并远程连接NAVCAT? <a class="header-anchor" href="#q-如何安装mysql并远程连接navcat" aria-hidden="true">#</a></h2><ol><li><h4 id="安装mysql" tabindex="-1">安装mysql <a class="header-anchor" href="#安装mysql" aria-hidden="true">#</a></h4><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki material-theme-palenight" tabindex="0"><code><span class="line"><span style="color:#A6ACCD;">sudo apt-get install mysql-server mysql-client</span></span>\n<span class="line"><span style="color:#A6ACCD;">// 执行初始化命令 用于设置密码</span></span>\n<span class="line"><span style="color:#A6ACCD;">mysql_secure_installation</span></span>\n<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div><p><a href="./Ubuntu服务器常见问题.html#安装提示">安装提示</a></p></li><li><h4 id="测试是否安装成功-下图为安装成功" tabindex="-1">测试是否安装成功(下图为安装成功) <a class="header-anchor" href="#测试是否安装成功-下图为安装成功" aria-hidden="true">#</a></h4><p><code>sudo netstat -tap | grep mysql</code><img src="https://c18e-1257416358.cos.accelerate.myqcloud.com/image-20230302100512774.png" alt="image-20230302100512774"></p></li><li><h4 id="配置远程可访问-配置完记得去云控制台规则开放3306端口" tabindex="-1">配置远程可访问（<strong>配置完记得去云控制台规则开放3306端口</strong>） <a class="header-anchor" href="#配置远程可访问-配置完记得去云控制台规则开放3306端口" aria-hidden="true">#</a></h4><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki material-theme-palenight" tabindex="0"><code><span class="line"><span style="color:#A6ACCD;"># 注意：不同 mysql 版本此配置文件位置和名字可能不同</span></span>\n<span class="line"><span style="color:#A6ACCD;">sudo vim /etc/mysql/mysql.conf.d/mysqld.cnf</span></span>\n<span class="line"><span style="color:#A6ACCD;"># 修改bind-address 和 mysqlx-bind-address</span></span>\n<span class="line"><span style="color:#A6ACCD;">bind-address            = 0.0.0.0</span></span>\n<span class="line"><span style="color:#A6ACCD;">mysqlx-bind-address     = 0.0.0.0</span></span>\n<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div></li><li><h4 id="重启服务并修改用户权限" tabindex="-1">重启服务并修改用户权限 <a class="header-anchor" href="#重启服务并修改用户权限" aria-hidden="true">#</a></h4><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki material-theme-palenight" tabindex="0"><code><span class="line"><span style="color:#A6ACCD;">sudo /etc/init.d/mysql restart</span></span>\n<span class="line"><span style="color:#A6ACCD;"># mysql 添加用户并赋予远程访问权限</span></span>\n<span class="line"><span style="color:#A6ACCD;">mysql -uroot -p;</span></span>\n<span class="line"><span style="color:#A6ACCD;">use mysql;</span></span>\n<span class="line"><span style="color:#A6ACCD;">create user &#39;USER&#39;@&#39;%&#39; identified by &#39;PASSWORD&#39;;</span></span>\n<span class="line"><span style="color:#A6ACCD;">grant all on *.* to &#39;USER&#39;@&#39;%&#39;;</span></span>\n<span class="line"><span style="color:#A6ACCD;">flush privileges;</span></span>\n<span class="line"><span style="color:#A6ACCD;">exit;</span></span>\n<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div></li><li><h4 id="测试是否配置成功" tabindex="-1">测试是否配置成功 <a class="header-anchor" href="#测试是否配置成功" aria-hidden="true">#</a></h4><p><code>netstat -nlt|grep 3306</code></p><p><img src="https://c18e-1257416358.cos.accelerate.myqcloud.com/image-20230302101335947.png" alt="image-20230302101335947"></p></li><li><h4 id="使用navcat-或者mysql访问数据库" tabindex="-1">使用navcat 或者mysql访问数据库 <a class="header-anchor" href="#使用navcat-或者mysql访问数据库" aria-hidden="true">#</a></h4><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki material-theme-palenight" tabindex="0"><code><span class="line"><span style="color:#A6ACCD;">mysql -h SERVER_IP -P 3306 -u root -p</span></span>\n<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div></li></ol><h2 id="q-如何安装python包管理工具-并使用虚拟目录管理环境" tabindex="-1">Q:如何安装python包管理工具，并使用虚拟目录管理环境？ <a class="header-anchor" href="#q-如何安装python包管理工具-并使用虚拟目录管理环境" aria-hidden="true">#</a></h2><ol><li><h4 id="安装-python-编译需要的依赖文件" tabindex="-1">安装 Python 编译需要的依赖文件 <a class="header-anchor" href="#安装-python-编译需要的依赖文件" aria-hidden="true">#</a></h4><div class="language-sh"><button title="Copy Code" class="copy"></button><span class="lang">sh</span><pre class="shiki material-theme-palenight" tabindex="0"><code><span class="line"><span style="color:#FFCB6B;">sudo</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">apt-get</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">install</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">-y</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">build-essential</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">libbz2-dev</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">libssl-dev</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">libreadline-dev</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">libsqlite3-dev</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">tk-dev</span></span>\n<span class="line"><span style="color:#676E95;font-style:italic;"># for Numpy, Matplotlib, SciPy, etc.</span></span>\n<span class="line"><span style="color:#FFCB6B;">sudo</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">apt-get</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">install</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">-y</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">libpng-dev</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">libfreetype6-dev</span><span style="color:#A6ACCD;"> </span></span>\n<span class="line"></span></code></pre></div></li><li><h4 id="安装-pyenv" tabindex="-1">安装 pyenv <a class="header-anchor" href="#安装-pyenv" aria-hidden="true">#</a></h4><div class="language-sh"><button title="Copy Code" class="copy"></button><span class="lang">sh</span><pre class="shiki material-theme-palenight" tabindex="0"><code><span class="line"><span style="color:#FFCB6B;">curl</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">-L</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">https://raw.githubusercontent.com/yyuu/pyenv-installer/master/bin/pyenv-installer</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">|</span><span style="color:#A6ACCD;"> </span><span style="color:#FFCB6B;">bash</span></span>\n<span class="line"></span></code></pre></div></li><li><h4 id="写入配置文件" tabindex="-1">写入配置文件 <a class="header-anchor" href="#写入配置文件" aria-hidden="true">#</a></h4><p>把以下内容写入 ~/.bashrc。如果后面不能使用，考虑放入 ~/.profile 或者 ~/.bash_profile</p><div class="language-sh"><button title="Copy Code" class="copy"></button><span class="lang">sh</span><pre class="shiki material-theme-palenight" tabindex="0"><code><span class="line"><span style="color:#C792EA;">export</span><span style="color:#A6ACCD;"> PYENV_ROOT</span><span style="color:#89DDFF;">=</span><span style="color:#89DDFF;">&quot;</span><span style="color:#A6ACCD;">$HOME</span><span style="color:#C3E88D;">/.pyenv</span><span style="color:#89DDFF;">&quot;</span></span>\n<span class="line"><span style="color:#C792EA;">export</span><span style="color:#A6ACCD;"> PATH</span><span style="color:#89DDFF;">=</span><span style="color:#89DDFF;">&quot;</span><span style="color:#A6ACCD;">$PYENV_ROOT</span><span style="color:#C3E88D;">/bin:</span><span style="color:#A6ACCD;">$PATH</span><span style="color:#89DDFF;">&quot;</span></span>\n<span class="line"><span style="color:#82AAFF;">eval</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">&quot;$(</span><span style="color:#FFCB6B;">pyenv</span><span style="color:#C3E88D;"> init -</span><span style="color:#89DDFF;">)&quot;</span></span>\n<span class="line"><span style="color:#82AAFF;">eval</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">&quot;$(</span><span style="color:#FFCB6B;">pyenv</span><span style="color:#C3E88D;"> virtualenv-init -</span><span style="color:#89DDFF;">)&quot;</span></span>\n<span class="line"></span></code></pre></div></li><li><h5 id="pyenv常用命令-python-md-python-版本管理工具" tabindex="-1">[pyenv常用命令](python.md#Python 版本管理工具) <a class="header-anchor" href="#pyenv常用命令-python-md-python-版本管理工具" aria-hidden="true">#</a></h5></li></ol><h2 id="q-服务器免密登录github配置" tabindex="-1"><a href="https://docs.github.com/zh/authentication/connecting-to-github-with-ssh/generating-a-new-ssh-key-and-adding-it-to-the-ssh-agent?platform=linux" target="_blank" rel="noreferrer">Q:服务器免密登录github配置</a> <a class="header-anchor" href="#q-服务器免密登录github配置" aria-hidden="true">#</a></h2><ol><li><h5 id="生成新-ssh-密钥" tabindex="-1">生成新 SSH 密钥 <a class="header-anchor" href="#生成新-ssh-密钥" aria-hidden="true">#</a></h5><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki material-theme-palenight" tabindex="0"><code><span class="line"><span style="color:#A6ACCD;">cd ~/.ssh &amp;&amp; ssh-keygen -t ed25519 -C &quot;your_email@example.com&quot;</span></span>\n<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div></li><li><h5 id="在后台启动-ssh-代理。" tabindex="-1">在后台启动 ssh 代理。 <a class="header-anchor" href="#在后台启动-ssh-代理。" aria-hidden="true">#</a></h5><div class="language-sh"><button title="Copy Code" class="copy"></button><span class="lang">sh</span><pre class="shiki material-theme-palenight" tabindex="0"><code><span class="line"><span style="color:#82AAFF;">eval</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">&quot;$(</span><span style="color:#FFCB6B;">ssh-agent</span><span style="color:#C3E88D;"> -s</span><span style="color:#89DDFF;">)&quot;</span></span>\n<span class="line"><span style="color:#FFCB6B;">输出</span></span>\n<span class="line"><span style="color:#89DDFF;">&gt;</span><span style="color:#A6ACCD;"> Agent pid </span><span style="color:#F78C6C;">59566</span></span>\n<span class="line"><span style="color:#FFCB6B;">ssh-add</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">~/.ssh/id_ed25519</span></span>\n<span class="line"></span></code></pre></div></li><li><h5 id="打开github添加ssh-key" tabindex="-1"><a href="https://github.com/settings/keys" target="_blank" rel="noreferrer">打开github添加ssh-key</a> <a class="header-anchor" href="#打开github添加ssh-key" aria-hidden="true">#</a></h5></li></ol><h2 id="q-如何解决ubuntu环境下uwsgi启动报错问题" tabindex="-1">Q：如何解决ubuntu环境下uwsgi启动报错问题？ <a class="header-anchor" href="#q-如何解决ubuntu环境下uwsgi启动报错问题" aria-hidden="true">#</a></h2><h4 id="uwsgi成功访问返回502" tabindex="-1">uwsgi成功访问返回502 <a class="header-anchor" href="#uwsgi成功访问返回502" aria-hidden="true">#</a></h4><div class="language-shell"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki material-theme-palenight" tabindex="0"><code><span class="line"><span style="color:#FFCB6B;">apt-get</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">install</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">uwsgi-plugin-python</span></span>\n<span class="line"></span></code></pre></div><h2 id="q-ubantu-如何日志文件常用操作有哪些" tabindex="-1">Q:Ubantu 如何日志文件常用操作有哪些？ <a class="header-anchor" href="#q-ubantu-如何日志文件常用操作有哪些" aria-hidden="true">#</a></h2><ol><li><p>查看日志文件前50行</p><div class="language-shell"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki material-theme-palenight" tabindex="0"><code><span class="line"><span style="color:#FFCB6B;">head</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">-n</span><span style="color:#A6ACCD;"> </span><span style="color:#F78C6C;">50</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">FILENAME</span></span>\n<span class="line"></span></code></pre></div></li><li><p>查看日志文件后50行</p><div class="language-shell"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki material-theme-palenight" tabindex="0"><code><span class="line"><span style="color:#FFCB6B;">tail</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">-n</span><span style="color:#A6ACCD;"> </span><span style="color:#F78C6C;">50</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">FILENAME</span></span>\n<span class="line"></span></code></pre></div></li><li><p>查看文件信息（文件大小为M）</p><div class="language-shell"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki material-theme-palenight" tabindex="0"><code><span class="line"><span style="color:#FFCB6B;">ls</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">-lh</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">FILENAME</span></span>\n<span class="line"></span></code></pre></div></li><li><p>清空日志文件</p><div class="language-shell"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki material-theme-palenight" tabindex="0"><code><span class="line"><span style="color:#82AAFF;">:</span><span style="color:#FFCB6B;">&gt;</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">FILENAME</span></span>\n<span class="line"></span></code></pre></div></li></ol><h2 id="q-如何申请免费证书" tabindex="-1">Q: 如何申请免费证书 <a class="header-anchor" href="#q-如何申请免费证书" aria-hidden="true">#</a></h2><p>服务器网站申请免费证书需要使用 <a href="https://github.com/acmesh-official/acme.sh" target="_blank" rel="noreferrer">acme.sh</a></p><h5 id="官方说明文档" tabindex="-1"><a href="https://github.com/acmesh-official/acme.sh/wiki/%E8%AF%B4%E6%98%8E" target="_blank" rel="noreferrer">官方说明文档</a> <a class="header-anchor" href="#官方说明文档" aria-hidden="true">#</a></h5><ol><li><h5 id="安装软件" tabindex="-1">安装软件 <a class="header-anchor" href="#安装软件" aria-hidden="true">#</a></h5><div class="language-shell"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki material-theme-palenight" tabindex="0"><code><span class="line"><span style="color:#FFCB6B;">curl</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">https://get.acme.sh</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">|</span><span style="color:#A6ACCD;"> </span><span style="color:#FFCB6B;">sh</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">-s</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">email=my@example.com</span></span>\n<span class="line"></span></code></pre></div></li><li><h5 id="配置dns" tabindex="-1">配置dns <a class="header-anchor" href="#配置dns" aria-hidden="true">#</a></h5><div class="language-shell"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki material-theme-palenight" tabindex="0"><code><span class="line"><span style="color:#89DDFF;">~</span><span style="color:#A6ACCD;">/.acme.sh/ </span><span style="color:#676E95;font-style:italic;"># 文件夹地址</span></span>\n<span class="line"><span style="color:#89DDFF;">~</span><span style="color:#A6ACCD;">/.acme.sh/account.conf  </span><span style="color:#676E95;font-style:italic;"># 账户配置地址</span></span>\n<span class="line"></span></code></pre></div></li><li><h5 id="生成证书" tabindex="-1">生成证书 <a class="header-anchor" href="#生成证书" aria-hidden="true">#</a></h5><div class="language-shell"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki material-theme-palenight" tabindex="0"><code><span class="line"><span style="color:#FFCB6B;">acme.sh</span><span style="color:#A6ACCD;">   </span><span style="color:#C3E88D;">--issue</span><span style="color:#A6ACCD;">   </span><span style="color:#C3E88D;">--dns</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">dns_dp</span><span style="color:#A6ACCD;">   </span><span style="color:#C3E88D;">-d</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">bleoty.com</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">-d</span><span style="color:#A6ACCD;"> </span><span style="color:#A6ACCD;">*</span><span style="color:#C3E88D;">.bleoty.com</span></span>\n<span class="line"></span></code></pre></div><h5 id="阿里云签发证书-唯一不同点-dns-ali" tabindex="-1">阿里云签发证书 唯一不同点 <a href="https://github.com/acmesh-official/acme.sh/wiki/dnsapi" target="_blank" rel="noreferrer">dns_ali</a> <a class="header-anchor" href="#阿里云签发证书-唯一不同点-dns-ali" aria-hidden="true">#</a></h5><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki material-theme-palenight" tabindex="0"><code><span class="line"><span style="color:#A6ACCD;">acme.sh   --issue   --dns dns_ali   -d shiniya.tigerzh.com </span></span>\n<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div></li><li><h5 id="nginx配置到具体目录并重启服务" tabindex="-1">nginx配置到具体目录并重启服务 <a class="header-anchor" href="#nginx配置到具体目录并重启服务" aria-hidden="true">#</a></h5><div class="language-shell"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki material-theme-palenight" tabindex="0"><code><span class="line"><span style="color:#FFCB6B;">acme.sh</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">--install-cert</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">-d</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">shiniya.cn</span><span style="color:#A6ACCD;"> \\</span></span>\n<span class="line"><span style="color:#A6ACCD;">--key-file       </span><span style="color:#C3E88D;">/etc/nginx/cert/shiniya.cn.key</span><span style="color:#A6ACCD;">  \\</span></span>\n<span class="line"><span style="color:#A6ACCD;">--fullchain-file </span><span style="color:#C3E88D;">/etc/nginx/cert/shiniya.cn.pem</span><span style="color:#A6ACCD;"> \\</span></span>\n<span class="line"><span style="color:#A6ACCD;">--reloadcmd     </span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">service nginx force-reload</span><span style="color:#89DDFF;">&quot;</span></span>\n<span class="line"></span></code></pre></div></li></ol><h2 id="q-ssh一直超时-nginx-网址一直超时" tabindex="-1">Q: SSH一直超时 nginx 网址一直超时 <a class="header-anchor" href="#q-ssh一直超时-nginx-网址一直超时" aria-hidden="true">#</a></h2><p><strong>不排除防火墙开启 未开启22端口的可能性</strong></p><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki material-theme-palenight" tabindex="0"><code><span class="line"><span style="color:#A6ACCD;"></span></span>\n<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div><h5 id="安装提示" tabindex="-1">安装提示 <a class="header-anchor" href="#安装提示" aria-hidden="true">#</a></h5><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki material-theme-palenight" tabindex="0"><code><span class="line"><span style="color:#A6ACCD;">// 保护MySQL服务器部署</span></span>\n<span class="line"><span style="color:#A6ACCD;">Securing the MySQL server deployment.</span></span>\n<span class="line"><span style="color:#A6ACCD;"></span></span>\n<span class="line"><span style="color:#A6ACCD;">// 使用空白密码连接到MySQL</span></span>\n<span class="line"><span style="color:#A6ACCD;">Connecting to MySQL using a blank password.</span></span>\n<span class="line"><span style="color:#A6ACCD;"></span></span>\n<span class="line"><span style="color:#A6ACCD;">验证密码组件可用于测试密码，</span></span>\n<span class="line"><span style="color:#A6ACCD;">提高安全性。它检查密码的强度，</span></span>\n<span class="line"><span style="color:#A6ACCD;">并且允许用户仅设置那些足够安全的密码。</span></span>\n<span class="line"><span style="color:#A6ACCD;">是否要设置验证密码组件？</span></span>\n<span class="line"><span style="color:#A6ACCD;">VALIDATE PASSWORD COMPONENT can be used to test passwords</span></span>\n<span class="line"><span style="color:#A6ACCD;">and improve security. It checks the strength of password</span></span>\n<span class="line"><span style="color:#A6ACCD;">and allows the users to set only those passwords which are</span></span>\n<span class="line"><span style="color:#A6ACCD;">secure enough. Would you like to setup VALIDATE PASSWORD component?</span></span>\n<span class="line"><span style="color:#A6ACCD;">// 按y或者Y表示是，按任何其他键表示否</span></span>\n<span class="line"><span style="color:#A6ACCD;">Press y|Y for Yes, any other key for No: n</span></span>\n<span class="line"><span style="color:#A6ACCD;"></span></span>\n<span class="line"><span style="color:#A6ACCD;"></span></span>\n<span class="line"><span style="color:#A6ACCD;"></span></span>\n<span class="line"><span style="color:#A6ACCD;">设置root的密码</span></span>\n<span class="line"><span style="color:#A6ACCD;">Please set the password for root here.</span></span>\n<span class="line"><span style="color:#A6ACCD;">New password:</span></span>\n<span class="line"><span style="color:#A6ACCD;"></span></span>\n<span class="line"><span style="color:#A6ACCD;">默认情况下，MySQL安装有匿名用户，</span></span>\n<span class="line"><span style="color:#A6ACCD;">允许任何人登录MySQL，而无需</span></span>\n<span class="line"><span style="color:#A6ACCD;">为他们创建的用户帐户。这仅用于测试，</span></span>\n<span class="line"><span style="color:#A6ACCD;">并使安装过程更加顺利。</span></span>\n<span class="line"><span style="color:#A6ACCD;">您应该在进入生产环境之前删除它们。</span></span>\n<span class="line"><span style="color:#A6ACCD;">删除匿名用户？建议 n</span></span>\n<span class="line"><span style="color:#A6ACCD;">By default, a MySQL installation has an anonymous user,</span></span>\n<span class="line"><span style="color:#A6ACCD;">allowing anyone to log into MySQL without having to have</span></span>\n<span class="line"><span style="color:#A6ACCD;">a user account created for them. This is intended only for testing,</span></span>\n<span class="line"><span style="color:#A6ACCD;">and to make the installation go a bit smoother.</span></span>\n<span class="line"><span style="color:#A6ACCD;">You should remove them before moving into a production environment.</span></span>\n<span class="line"><span style="color:#A6ACCD;"></span></span>\n<span class="line"><span style="color:#A6ACCD;">Remove anonymous users? (Press y|Y for Yes, any other key for No) : n</span></span>\n<span class="line"><span style="color:#A6ACCD;"></span></span>\n<span class="line"><span style="color:#A6ACCD;">Normally, root should only be allowed to connect from &#39;localhost&#39;. This ensures that someone cannot guess at the root             password from the network. Disallow root login remotely? (Press y|Y for Yes, any other key for No) : n</span></span>\n<span class="line"><span style="color:#A6ACCD;">By default, MySQL comes with a database named &#39;test&#39; that</span></span>\n<span class="line"><span style="color:#A6ACCD;">anyone can access. This is also intended only for testing,</span></span>\n<span class="line"><span style="color:#A6ACCD;">and should be removed before moving into a production</span></span>\n<span class="line"><span style="color:#A6ACCD;">environment.</span></span>\n<span class="line"><span style="color:#A6ACCD;"></span></span>\n<span class="line"><span style="color:#A6ACCD;">Remove test database and access to it? (Press y|Y for Yes, any other key for No) : n</span></span>\n<span class="line"><span style="color:#A6ACCD;">Reloading the privilege tables will ensure that all changes</span></span>\n<span class="line"><span style="color:#A6ACCD;">made so far will take effect immediately.</span></span>\n<span class="line"><span style="color:#A6ACCD;"></span></span>\n<span class="line"><span style="color:#A6ACCD;">Reload privilege tables now? (Press y|Y for Yes, any other key for No) : y</span></span>\n<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div>',26)];const t=s(p,[["render",function(s,l,e,p,t,c){return a(),n("div",null,o)}]]);export{e as __pageData,t as default};
